{
  "format": {
    "structure": [
      "Introduction to the topic",
      "Key questions to explore:",
      "1. [First key question]",
      "2. [Second key question]",
      "3. [Third key question]",
      "4. [Fourth key question]",
      "Initial analysis and context",
      "Suggested research directions"
    ],
    "style": "academic but accessible",
    "length": "comprehensive but concise"
  },
  "question_generation": {
    "requirements": [
      "Cover different aspects of the topic",
      "Include historical context",
      "Consider cultural significance",
      "Address contemporary relevance",
      "Explore interdisciplinary connections"
    ],
    "perspectives": [
      "Historical",
      "Cultural",
      "Technical",
      "Philosophical",
      "Contemporary"
    ]
  },
  "references": {
    "count": {
      "min": 2,
      "max": 3
    },
    "types": [
      "academic sources",
      "historical texts",
      "cultural documents"
    ],
    "format": "APA",
    "requirements": [
      "Include at least one primary source",
      "Ensure diverse perspectives",
      "Prioritize authoritative sources"
    ]
  },
  "quality_standards": {
    "fact_checking": "required",
    "cross_referencing": "required",
    "cultural_sensitivity": "high",
    "balance": "include multiple perspectives"
  },
  "output_requirements": {
    "language": "clear and precise",
    "tone": "respectful and scholarly",
    "avoid": [
      "speculation without evidence",
      "cultural appropriation",
      "simplistic generalizations"
    ]
  }
}

{
  "format": "YYYY-MM-DD: @username - Brief summary",
  "requirements": ["date", "username", "summary"],
  "validation": {
    "date": {
      "pattern": "\\d{4}-\\d{2}-\\d{2}",
      "message": "Date must be in YYYY-MM-DD format"
    },
    "username": {
      "pattern": "@\\w+",
      "message": "Username must be prefixed with @"
    },
    "summary": {
      "minLength": 10,
      "message": "Summary must be at least 10 characters"
    }
  }
}

{
