      }
    }
  }
}

{
  "format": "![YOUTUBE src=https://www.youtube.com/embed/{videoId}]{videoTitle}[END YOUTUBE]",
  "requirements": ["videoId", "videoTitle", "description", "originalUrl"],
  "validation": {
    "videoId": {
      "pattern": "[a-zA-Z0-9_-]{11}",
      "message": "Video ID must be a valid YouTube video ID (11 characters)"
    },
    "videoTitle": {
      "minLength": 1,
      "maxLength": 100,
      "message": "Video title must be between 1 and 100 characters"
    },
    "description": {
      "minLength": 10,
      "maxLength": 500,
      "message": "Description must be between 10 and 500 characters"
    },
    "originalUrl": {
      "pattern": "^(https://(www\\.)?(youtube\\.com/watch\\?v=|youtu\\.be/)[a-zA-Z0-9_-]{11})",
      "message": "Original URL must be a valid YouTube URL"
    }
  },
  "nodeStructure": {
    "type": "youtube-video",
    "color": "#FF0000",
    "imageWidth": "100%",
    "imageHeight": "100%",
    "visible": true,
    "path": ""
  },
  "infoFormat": "[SECTION | background-color:'#FFF'; color:'#333']{description}[END SECTION]"
}

{
  "format": {
    "types": {
      "flow_chart": {
        "syntax": "graph TD",
        "structure": [
          "graph TD",
          "[Node Label] --> [Connected Node]",
          "[Node Label] --> [Connected Node]"
        ],
        "style": "clear and hierarchical"
      },
      "gantt_chart": {
        "syntax": "gantt",
        "structure": [
          "gantt",
          "title [Title]",
          "dateFormat YYYY-MM-DD",
          "section [Section Name]",
          "[Task Name] :[status], [id], [start-date], [duration]"
        ],
        "style": "chronological and organized"
      },
      "quadrant_chart": {
        "syntax": "quadrantChart",
        "structure": [
          "quadrantChart",
          "title [Title]",
          "x-axis [Low Label] --> [High Label]",
          "y-axis [Low Label] --> [High Label]",
          "quadrant-1 [Description]",
          "quadrant-2 [Description]",
          "quadrant-3 [Description]",
          "quadrant-4 [Description]",
          "[Item]: [x-value, y-value]"
        ],
        "style": "analytical and clear"
      }
    }
  },
  "validation": {
    "common": {
      "title": "required",
      "indentation": "4 spaces",
      "max_items": 50
    },
    "specific": {
      "flow_chart": {
        "max_nodes": 30,
        "max_connections": 50
      },
      "gantt_chart": {
        "date_format": "YYYY-MM-DD",
        "max_tasks": 20
      },
      "quadrant_chart": {
        "value_range": "0 to 1",
        "max_points": 20
      }
    }
  },
  "output_requirements": {
    "language": "clear and precise",
    "tone": "professional",
    "avoid": [
      "ambiguous labels",
      "overly complex structures",
      "missing required elements"
    ]
  }
}

{
  "format": {
    "structure": {
      "type": "fulltext",
      "color": "#D2691E",
      "imageWidth": "100%",
      "imageHeight": "100%",
      "visible": true
    },
    "data_format": {
      "info": {
        "sections": [
          {
            "type": "title",
            "format": "[FANCY | font-size: 4.5em; color: #2c3e50; text-align: center]",
            "required": true
          },
          {
            "type": "header_image",
            "format": "![Header|height: 200px; object-fit: cover; object-position: center]",
            "required": true
          },
          {
            "type": "introduction",
            "format": "## Introduction\n\n[SECTION | background-color:#FFFBE6; color:#000]",
            "required": true
          },
          {
            "type": "quote",
            "format": "[QUOTE | Cited=Author Name]",
            "required": false
          },
          {
            "type": "key_points",
            "format": "## Key Points\n\n1. ",
            "required": true
          },
          {
            "type": "additional_section",
            "format": "[SECTION | background-color:#E7F7F7; color:#111]",
            "required": false
          }
        ]
      }
    }
  },
  "validation": {
    "required_fields": [
      "label",
      "info",
      "type",
      "color"
    ],
    "sections": {
      "min": 3,
      "max": 6
    },
    "content": {
      "min_length": 100,
      "max_length": 5000
    }
  }
}

